package com.syntax.class02;

public class HomeworkReview {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//to create a variable: datatype and name
		
				//whole numbers
				/*
				 * box1: is variable
				 * to create a variable we need 2 things: datatype and name of container
				 *
				 */
		
				byte box1= 21;
				short box2= 2022;
				int box3=76666; //most common
				long box4=45678909846543L;
				
				//decimal values

				float container=1.99F;
				double container2=89.99;
				
				
				// Non-numeric: to represent 1 character
				
				char gender='m';
				char dollar='$';
				
				// Non-numeric: to represent true or false (yes or no)
				
				boolean hungry=true;
				boolean sleepy=false;
				
				//how to access values from variables (containers/boxes)?
				
				System.out.println(container2);//do not use double quotes to access variables
				
				//Access and print values from all variables
				
				
				System.out.println(box1);
				
				System.out.println(box2);
				
				System.out.println(box3);
				
				System.out.println(box4);
				
				System.out.println(container);
				
				System.out.println(container2);
				
				System.out.println(gender);
				
				System.out.println(dollar);
				
				System.out.println(hungry);
				
				System.out.println(sleepy);
				
				
				int year=2022; // create a variable and store value
				
				System.out.println(year);
				
				
				year=2023; // reassigning value of same box (variable) 
				System.out.println(year);
	}

}
